import { useState, useCallback, useEffect} from "react"
import { useHttp } from "./http.hooks"

const storageName = 'userData'

export const useAuth = () =>{
    const [token,setToken] = useState(null)
    const [ready,setReady] = useState(false)
    const [userId,setUserId] = useState(null)
    const [decoded,setDecoded] = useState('')
    const {request} = useHttp()

    const getVerify = useCallback( async () => {
        try{
          const data = await request(`/api/data/getData/${auth.userId}`,'GET',null,{ 
                Authorization: `Bearer ${auth.token}`
            })
            
            return data
        }catch(e){
        }
      
    }, [auth.token,auth.userId, request])

    const login = useCallback( (jwtToken, id, data) =>{
        setToken(jwtToken)
        setUserId(id)
        setDecoded(data)


        localStorage.setItem(storageName,JSON.stringify({
            userId:id,
            token:jwtToken,
           // decoded:data
        }))
    },[])

    const logout = useCallback( () => {
        setToken(null)
        setUserId(null)
        setDecoded(null)
        localStorage.removeItem(storageName)
    },[])

    useEffect(() =>{
        const data = JSON.parse(localStorage.getItem(storageName))

        if (data && data.token){
           
            const temp = data.token

            const decoded = request('api/data/verify','POST', {temp} ) 
            
        login(data.token, data.userId, decoded)            
        }
        setReady(true)
    }, [login,request])

    return {login,logout,token,userId,decoded,ready}
}
