                </Route>
    
                <Redirect to="/adminPage" />
                </Switch>
                )
        } */


    }

    return (
        <Switch>
        <Route path ="/login" exact>
            <AuthPage />
        </Route>

        <Route path = "/register" exact>
            <RegisterPage />
        </Route>

        <Route path ="/admin" exact>
            <AdminAuthPage/>
        </Route>

        <Redirect to="/login"/>
        </Switch>
    )

}
