import React, { useContext, useState, useCallback, useEffect } from 'react'
import { AuthContext } from '../context/AuthContext'
import { useHttp } from '../hooks/http.hooks'
import { get } from 'mongoose'
import { Loader } from '../components/Loader'


export const ClientPage = () => {
    const [user,setUser] = useState(null) 
    const [notification,setNotification] = useState('')     
    
    const auth = useContext(AuthContext)
    const {loading, request} = useHttp() 

    const notificationHandler = async() =>{
        try{
          //  const user = await request('api/data/generate', 'POST',{ } , { Authorization: 'Bearer ${auth.token}'}) // ???
        } catch(e) {

        }
    } 

    const getUser = useCallback( async () => {
        try{
          const data = await request('/api/getData/${auth.userId}','GET',null,{ 
                Authorization: 'Bearer ${auth.token}'
            })
            setUser(data)
        }catch(e){
        }
      
    }, [auth.token,auth.userId, request])

    useEffect( () => {
        getUser()
    }, [getUser])

    if (loading) {
        return <Loader/>
    }

    

    return(

        <div className="collection">
            <ul className="collection with-header">
                <li className="collection-header"><h4>First Names</h4></li>
                <li className="collection-item">Alvin</li>
                
            </ul>
            <button className="waves-effect waves-light btn"
                disabled = {loading}
                onClick={notificationHandler}
                >
                Запросить помощь
                </button> 
      </div>
    )

}
