import React, { useContext } from 'react'
import {Switch,Route, Redirect} from 'react-router-dom'
import {ClientPage} from './pages/ClientPage'
import { AuthPage } from './pages/AuthPage'
import { RegisterPage } from './pages/RegisterPage'
import { AdminAuthPage } from './pages/AdminAuthPage'
import {AdminPage} from './pages/AdminPage'
import { useHttp } from './hooks/http.hooks'
import { AuthContext } from './context/AuthContext'

export const useRoutes = (isAuthenticated,decoded) => { 

    const {request} = useHttp()
    const auth = useContext(AuthContext)

    const routesHandler = async() =>{
        try{
            
            const temp = auth.token

            const data = await request('api/data/verify','POST', {temp} )

            return data

        } catch(e) {

        }
    } 

    if (isAuthenticated){

       if (decoded === 'client')
        {
        return (
            <Switch>
            <Route path = "/clientPage" exact>
                <ClientPage/>
            </Route>

            <Redirect to="/clientPage" />
            </Switch>
            )
        }

        if (decoded === 'admin')
        {
            return (
                <Switch>
                <Route path = "/adminPage" exact>
                    <AdminPage/>
                </Route>

                
                <Route path = "/register" exact>
                    <RegisterPage />
                </Route>
    
                <Redirect to="/adminPage" />
                </Switch>
                )
        } 

    }

    return (
        <Switch>
        <Route path ="/login" exact>
            <AuthPage />
        </Route>

        <Route path ="/admin" exact>
            <AdminAuthPage/>
        </Route>

        <Redirect to="/login"/>
        </Switch>
    )

}
